import React, { useState } from 'react';
import './AdminDashboard.css';

const AdminDashboard = () => {
  // Состояния для вкладок
  const [activeTab, setActiveTab] = useState('teachers');
  const [showAddModal, setShowAddModal] = useState(false);
  const [modalType, setModalType] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  // Моковые данные
  const [teachers, setTeachers] = useState([
    { id: 1, name: 'Иванова Мария Петровна', subject: 'Математика', room: '201' },
    { id: 2, name: 'Петров Иван Сергеевич', subject: 'Физика', room: '305' },
  ]);

  const [students, setStudents] = useState([
    { id: 1, name: 'Смирнов Алексей', class: '10А' },
    { id: 2, name: 'Кузнецова Анна', class: '9Б' },
  ]);

  const [grades, setGrades] = useState([
    { id: 1, student: 'Смирнов Алексей', subject1: 5, subject2: 4, subject3: 5 },
    { id: 2, student: 'Кузнецова Анна', subject1: 3, subject2: 4, subject3: 3 },
  ]);

  // Состояния для форм
  const [newTeacher, setNewTeacher] = useState({ name: '', subject: '', room: '' });
  const [newStudent, setNewStudent] = useState({ name: '', class: '' });
  const [newGrades, setNewGrades] = useState({ student: '', subject1: '', subject2: '', subject3: '' });

  // Обработчики
  const handleAdd = (type) => {
    setModalType(type);
    setShowAddModal(true);
  };

  const handleDelete = (id, type) => {
    if (type === 'teacher') {
      setTeachers(teachers.filter(teacher => teacher.id !== id));
    } else if (type === 'student') {
      setStudents(students.filter(student => student.id !== id));
    } else {
      setGrades(grades.filter(grade => grade.id !== id));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (modalType === 'teacher') {
      setTeachers([...teachers, { ...newTeacher, id: teachers.length + 1 }]);
    } else if (modalType === 'student') {
      setStudents([...students, { ...newStudent, id: students.length + 1 }]);
    } else {
      setGrades([...grades, { ...newGrades, id: grades.length + 1 }]);
    }
    setShowAddModal(false);
    resetForm();
  };

  const resetForm = () => {
    setNewTeacher({ name: '', subject: '', room: '' });
    setNewStudent({ name: '', class: '' });
    setNewGrades({ student: '', subject1: '', subject2: '', subject3: '' });
  };

  // Фильтрация данных
  const filteredTeachers = teachers.filter(teacher => 
    teacher.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    teacher.subject.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const filteredStudents = students.filter(student => 
    student.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    student.class.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const filteredGrades = grades.filter(grade => 
    grade.student.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Статистика
  const calculateStatistics = () => {
    // Здесь будет логика расчета статистики
    return {
      worstSubject: 'Физика',
      failingStudents: 5,
      worstTeacher: 'Петров И.С.',
      classAverages: { '10А': 4.2, '9Б': 3.8 },
      bestClass: '10А',
      worstClass: '9Б'
    };
  };

  const stats = calculateStatistics();

  return (
    <div className="admin-dashboard">
      <header className="dashboard-header">
        <h1>Панель завуча</h1>
        <div className="search-box">
          <input
            type="text"
            placeholder="Поиск..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <i className="fas fa-search"></i>
        </div>
      </header>

      <div className="dashboard-container">
        <nav className="sidebar">
          <ul>
            <li 
              className={activeTab === 'teachers' ? 'active' : ''}
              onClick={() => setActiveTab('teachers')}
            >
              <i className="fas fa-chalkboard-teacher"></i> Учителя
            </li>
            <li 
              className={activeTab === 'students' ? 'active' : ''}
              onClick={() => setActiveTab('students')}
            >
              <i className="fas fa-user-graduate"></i> Ученики
            </li>
            <li 
              className={activeTab === 'grades' ? 'active' : ''}
              onClick={() => setActiveTab('grades')}
            >
              <i className="fas fa-clipboard-list"></i> Успеваемость
            </li>
            <li 
              className={activeTab === 'stats' ? 'active' : ''}
              onClick={() => setActiveTab('stats')}
            >
              <i className="fas fa-chart-bar"></i> Статистика
            </li>
          </ul>
        </nav>

        <main className="content">
          {activeTab === 'teachers' && (
            <div className="table-section">
              <div className="section-header">
                <h2>Список учителей</h2>
                <button className="add-btn" onClick={() => handleAdd('teacher')}>
                  <i className="fas fa-plus"></i> Добавить учителя
                </button>
              </div>
              <div className="table-container">
                <table>
                  <thead>
                    <tr>
                      <th>ID</th>
                      <th>ФИО</th>
                      <th>Предмет</th>
                      <th>Кабинет</th>
                      <th>Действия</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredTeachers.map(teacher => (
                      <tr key={teacher.id}>
                        <td>{teacher.id}</td>
                        <td>{teacher.name}</td>
                        <td>{teacher.subject}</td>
                        <td>{teacher.room}</td>
                        <td>
                          <button className="edit-btn"><i className="fas fa-edit"></i></button>
                          <button 
                            className="delete-btn"
                            onClick={() => handleDelete(teacher.id, 'teacher')}
                          >
                            <i className="fas fa-trash"></i>
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}

          {activeTab === 'students' && (
            <div className="table-section">
              <div className="section-header">
                <h2>Список учеников</h2>
                <button className="add-btn" onClick={() => handleAdd('student')}>
                  <i className="fas fa-plus"></i> Добавить ученика
                </button>
              </div>
              <div className="table-container">
                <table>
                  <thead>
                    <tr>
                      <th>ID</th>
                      <th>ФИО</th>
                      <th>Класс</th>
                      <th>Действия</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredStudents.map(student => (
                      <tr key={student.id}>
                        <td>{student.id}</td>
                        <td>{student.name}</td>
                        <td>{student.class}</td>
                        <td>
                          <button className="edit-btn"><i className="fas fa-edit"></i></button>
                          <button 
                            className="delete-btn"
                            onClick={() => handleDelete(student.id, 'student')}
                          >
                            <i className="fas fa-trash"></i>
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}

          {activeTab === 'grades' && (
            <div className="table-section">
              <div className="section-header">
                <h2>Успеваемость</h2>
                <button className="add-btn" onClick={() => handleAdd('grades')}>
                  <i className="fas fa-plus"></i> Добавить оценки
                </button>
              </div>
              <div className="table-container">
                <table>
                  <thead>
                    <tr>
                      <th>ID</th>
                      <th>Ученик</th>
                      <th>Математика</th>
                      <th>Физика</th>
                      <th>Литература</th>
                      <th>Действия</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredGrades.map(grade => (
                      <tr key={grade.id}>
                        <td>{grade.id}</td>
                        <td>{grade.student}</td>
                        <td>{grade.subject1}</td>
                        <td>{grade.subject2}</td>
                        <td>{grade.subject3}</td>
                        <td>
                          <button className="edit-btn"><i className="fas fa-edit"></i></button>
                          <button 
                            className="delete-btn"
                            onClick={() => handleDelete(grade.id, 'grade')}
                          >
                            <i className="fas fa-trash"></i>
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}

          {activeTab === 'stats' && (
            <div className="stats-section">
              <h2>Статистика успеваемости</h2>
              
              <div className="stats-grid">
                <div className="stat-card">
                  <div className="stat-icon bg-red">
                    <i className="fas fa-exclamation-triangle"></i>
                  </div>
                  <div className="stat-info">
                    <h3>Самый сложный предмет</h3>
                    <p>{stats.worstSubject}</p>
                  </div>
                </div>

                <div className="stat-card">
                  <div className="stat-icon bg-orange">
                    <i className="fas fa-user-times"></i>
                  </div>
                  <div className="stat-info">
                    <h3>Неуспевающие ученики</h3>
                    <p>{stats.failingStudents}</p>
                  </div>
                </div>

                <div className="stat-card">
                  <div className="stat-icon bg-purple">
                    <i className="fas fa-chalkboard-teacher"></i>
                  </div>
                  <div className="stat-info">
                    <h3>Самый строгий учитель</h3>
                    <p>{stats.worstTeacher}</p>
                  </div>
                </div>

                <div className="stat-card">
                  <div className="stat-icon bg-blue">
                    <i className="fas fa-users"></i>
                  </div>
                  <div className="stat-info">
                    <h3>Средние оценки по классам</h3>
                    <ul>
                      {Object.entries(stats.classAverages).map(([cls, avg]) => (
                        <li key={cls}>{cls}: {avg.toFixed(1)}</li>
                      ))}
                    </ul>
                  </div>
                </div>

                <div className="stat-card">
                  <div className="stat-icon bg-green">
                    <i className="fas fa-trophy"></i>
                  </div>
                  <div className="stat-info">
                    <h3>Лучший класс</h3>
                    <p>{stats.bestClass}</p>
                  </div>
                </div>

                <div className="stat-card">
                  <div className="stat-icon bg-red">
                    <i className="fas fa-sad-tear"></i>
                  </div>
                  <div className="stat-info">
                    <h3>Худший класс</h3>
                    <p>{stats.worstClass}</p>
                  </div>
                </div>
              </div>
            </div>
          )}
        </main>
      </div>

      {/* Модальные окна */}
      {showAddModal && (
        <div className="modal-overlay">
          <div className="modal">
            <button className="close-modal" onClick={() => setShowAddModal(false)}>
              <i className="fas fa-times"></i>
            </button>
            
            <h2>
              {modalType === 'teacher' && 'Добавить учителя'}
              {modalType === 'student' && 'Добавить ученика'}
              {modalType === 'grades' && 'Добавить оценки'}
            </h2>
            
            <form onSubmit={handleSubmit}>
              {modalType === 'teacher' && (
                <>
                  <div className="form-group">
                    <label>ФИО</label>
                    <input 
                      type="text" 
                      value={newTeacher.name}
                      onChange={(e) => setNewTeacher({...newTeacher, name: e.target.value})}
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label>Предмет</label>
                    <input 
                      type="text" 
                      value={newTeacher.subject}
                      onChange={(e) => setNewTeacher({...newTeacher, subject: e.target.value})}
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label>Кабинет</label>
                    <input 
                      type="text" 
                      value={newTeacher.room}
                      onChange={(e) => setNewTeacher({...newTeacher, room: e.target.value})}
                      required
                    />
                  </div>
                </>
              )}

              {modalType === 'student' && (
                <>
                  <div className="form-group">
                    <label>ФИО</label>
                    <input 
                      type="text" 
                      value={newStudent.name}
                      onChange={(e) => setNewStudent({...newStudent, name: e.target.value})}
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label>Класс</label>
                    <input 
                      type="text" 
                      value={newStudent.class}
                      onChange={(e) => setNewStudent({...newStudent, class: e.target.value})}
                      required
                    />
                  </div>
                </>
              )}

              {modalType === 'grades' && (
                <>
                  <div className="form-group">
                    <label>Ученик</label>
                    <select
                      value={newGrades.student}
                      onChange={(e) => setNewGrades({...newGrades, student: e.target.value})}
                      required
                    >
                      <option value="">Выберите ученика</option>
                      {students.map(student => (
                        <option key={student.id} value={student.name}>
                          {student.name} ({student.class})
                        </option>
                      ))}
                    </select>
                  </div>
                  <div className="form-group">
                    <label>Математика</label>
                    <select
                      value={newGrades.subject1}
                      onChange={(e) => setNewGrades({...newGrades, subject1: e.target.value})}
                      required
                    >
                      <option value="">Выберите оценку</option>
                      <option value="5">5</option>
                      <option value="4">4</option>
                      <option value="3">3</option>
                      <option value="2">2</option>
                    </select>
                  </div>
                  <div className="form-group">
                    <label>Физика</label>
                    <select
                      value={newGrades.subject2}
                      onChange={(e) => setNewGrades({...newGrades, subject2: e.target.value})}
                      required
                    >
                      <option value="">Выберите оценку</option>
                      <option value="5">5</option>
                      <option value="4">4</option>
                      <option value="3">3</option>
                      <option value="2">2</option>
                    </select>
                  </div>
                  <div className="form-group">
                    <label>Литература</label>
                    <select
                      value={newGrades.subject3}
                      onChange={(e) => setNewGrades({...newGrades, subject3: e.target.value})}
                      required
                    >
                      <option value="">Выберите оценку</option>
                      <option value="5">5</option>
                      <option value="4">4</option>
                      <option value="3">3</option>
                      <option value="2">2</option>
                    </select>
                  </div>
                </>
              )}

              <div className="form-actions">
                <button type="button" className="cancel-btn" onClick={() => setShowAddModal(false)}>
                  Отмена
                </button>
                <button type="submit" className="submit-btn">
                  Сохранить
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
