import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';

import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const API_URL = "http://localhost:5000/api";
  const navigate = useNavigate();

  // Состояния для управления интерфейсом
  const [activeTab, setActiveTab] = useState('teachers');
  const [showAddModal, setShowAddModal] = useState(false);
  const [showLogoutModal, setShowLogoutModal] = useState(false);
  const [modalType, setModalType] = useState('');
  const [editId, setEditId] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  // Основные данные
  const [teachers, setTeachers] = useState([]);
  const [students, setStudents] = useState([]);
  const [grades, setGrades] = useState([]);
  const [users, setUsers] = useState([]);
  const [stats, setStats] = useState({
    worstSubject: '',
    failingStudents: 0,
    worstTeacher: '',
    classAverages: {},
    bestClass: '',
    worstClass: ''
  });

  // Состояния для форм
  const [newTeacher, setNewTeacher] = useState({
    name: '',
    subject: '',
    room: '',
    userId: ''  // Добавление userId для привязки учителя к пользователю
  });
  const [newStudent, setNewStudent] = useState({
    name: '',
    class: ''
  });
  const [newGrades, setNewGrades] = useState({
    student: '',
    subject1: '',
    subject2: '',
    subject3: ''
  });
  const [newUser, setNewUser] = useState({
    username: '',
    password: '',
    confirmPassword: '',
    role: 'User'
  });

  // Пагинация
  const [pagination, setPagination] = useState({
    teachers: { page: 1, perPage: 10, total: 0 },
    students: { page: 1, perPage: 10, total: 0 },
    grades: { page: 1, perPage: 10, total: 0 },
    users: { page: 1, perPage: 10, total: 0 }
  });

  // Фильтры
  const [filters, setFilters] = useState({
    teachers: { subject: '', room: '' },
    students: { class: '' },
    grades: { subject: '', minGrade: '', maxGrade: '' },
    users: { role: '' }
  });

  // Проверка авторизации и роли
  useEffect(() => {
    const token = localStorage.getItem('authToken');
    if (!token) {
      navigate('/');
      toast.warning('Доступ запрещен. Требуется вход в систему.');
      return;
    }
    const { role } = jwtDecode(token);
    if (role !== 'Admin') {
      navigate('/');
      toast.warning('Доступ запрещен. Требуются права администратора.');
    }
  }, [navigate]);

  // Загрузка данных при монтировании и смене вкладки
  useEffect(() => {
    const loadData = async () => {
      try {
        setIsLoading(true);
        switch (activeTab) {
          case 'teachers':
            await fetchTeachers();
            break;
          case 'students':
            await fetchStudents();
            break;
          case 'grades':
            await fetchGrades();
            break;
          case 'stats':
            await fetchStats();
            break;
          case 'users':
            await fetchUsers();
            break;
          default:
            break;
        }
      } catch (err) {
        setError(err.message);
        toast.error(`Ошибка загрузки данных: ${err.message}`);
      } finally {
        setIsLoading(false);
      }
    };

    loadData();
  }, [activeTab]);

  // API функции
  const fetchTeachers = async (page = 1) => {
    try {
      const response = await axios.get(`${API_URL}/teacher`, {
        params: {
          page,
          limit: pagination.teachers.perPage,
          search: searchTerm,
          ...filters.teachers
        },
        headers: {
          Authorization: `Bearer ${localStorage.getItem('authToken')}`
        }
      });
      setTeachers(response.data.teachers);
      setPagination(prev => ({
        ...prev,
        teachers: {
          ...prev.teachers,
          page,
          total: response.data.totalCount
        }
      }));
    } catch (err) {
      toast.error('Ошибка загрузки учителей');
      throw err;
    }
  };

  const fetchStudents = async (page = 1) => {
    try {
      const response = await axios.get(`${API_URL}/student`, {
        params: {
          page,
          limit: pagination.students.perPage,
          search: searchTerm,
          ...filters.students
        },
        headers: {
          Authorization: `Bearer ${localStorage.getItem('authToken')}`
        }
      });
      setStudents(response.data.students);
      setPagination(prev => ({
        ...prev,
        students: {
          ...prev.students,
          page,
          total: response.data.totalCount
        }
      }));
    } catch (err) {
      toast.error('Ошибка загрузки студентов');
      throw err;
    }
  };

  const fetchGrades = async (page = 1) => {
    try {
      const response = await axios.get(`${API_URL}/grade`, {
        params: {
          page,
          limit: pagination.grades.perPage,
          search: searchTerm,
          ...filters.grades
        },
        headers: {
          Authorization: `Bearer ${localStorage.getItem('authToken')}`
        }
      });
      setGrades(response.data.grades);
      setPagination(prev => ({
        ...prev,
        grades: {
          ...prev.grades,
          page,
          total: response.data.totalCount
        }
      }));
    } catch (err) {
      toast.error('Ошибка загрузки оценок');
      throw err;
    }
  };

  const fetchUsers = async (page = 1) => {
    try {
      const response = await axios.get(`${API_URL}/user`, {
        params: {
          page,
          limit: pagination.users.perPage,
          search: searchTerm,
          role: filters.users.role
        },
        headers: {
          Authorization: `Bearer ${localStorage.getItem('authToken')}`
        }
      });
      setUsers(response.data);
      setPagination(prev => ({
        ...prev,
        users: {
          ...prev.users,
          page,
          total: parseInt(response.headers['x-total-count']) || 0
        }
      }));
    } catch (err) {
      toast.error('Ошибка загрузки пользователей');
      throw err;
    }
  };

  const fetchStats = async () => {
    try {
      const [failing, worstTeacher, classStats] = await Promise.all([
        axios.get(`${API_URL}/report/failing`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` }
        }),
        axios.get(`${API_URL}/report/worst-teacher`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` }
        }),
        axios.get(`${API_URL}/report/class`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` }
        })
      ]);

      setStats({
        worstSubject: 'Физика',
        failingStudents: failing.data.count || 0,
        worstTeacher: worstTeacher.data[0]?.name || 'Нет данных',
        classAverages: classStats.data.reduce((acc, curr) => {
          acc[curr.className] = curr.average;
          return acc;
        }, {}),
        bestClass: classStats.data.length > 0
          ? classStats.data.reduce((prev, current) =>
            (prev.average > current.average) ? prev : current
          ).className
          : 'Нет данных',
        worstClass: classStats.data.length > 0
          ? classStats.data.reduce((prev, current) =>
            (prev.average < current.average) ? prev : current
          ).className
          : 'Нет данных'
      });
    } catch (err) {
      toast.error('Ошибка загрузки статистики');
      throw err;
    }
  };

  // Функция для добавления/редактирования (обработчик формы)
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setIsLoading(true);
      setError(null);

      if (modalType === 'teacher' || modalType === 'edit_teacher') {
        if (!newTeacher.name.trim() || !newTeacher.subject.trim() || !newTeacher.room.trim() || !newTeacher.userId) {
          toast.error('Заполните все поля для учителя');
          return;
        }

        if (modalType === 'teacher') {
          await axios.post(
            `${API_URL}/teacher`,
            {
              FullName: newTeacher.name,
              Subject: newTeacher.subject,
              ClassroomNumber: newTeacher.room,
              UserID: newTeacher.userId  // Добавляем UserID
            },
            {
              headers: {
                Authorization: `Bearer ${localStorage.getItem('authToken')}`,
                'Content-Type': 'application/json'
              }
            }
          );
          toast.success('Учитель успешно создан');
        } else {
          await axios.put(
            `${API_URL}/teacher/${editId}`,
            {
              FullName: newTeacher.name,
              Subject: newTeacher.subject,
              ClassroomNumber: newTeacher.room,
              UserID: newTeacher.userId
            },
            {
              headers: {
                Authorization: `Bearer ${localStorage.getItem('authToken')}`,
                'Content-Type': 'application/json'
              }
            }
          );
          toast.success('Данные учителя успешно обновлены');
        }
        await fetchTeachers(pagination.teachers.page);
      }

      else if (modalType === 'student' || modalType === 'edit_student') {
        if (!newStudent.name.trim() || !newStudent.class) {
          toast.error('Заполните все поля для ученика');
          return;
        }

        if (modalType === 'student') {
          await axios.post(
            `${API_URL}/student`,
            {
              FullName: newStudent.name,
              ClassName: newStudent.class
            },
            {
              headers: {
                Authorization: `Bearer ${localStorage.getItem('authToken')}`,
                'Content-Type': 'application/json'
              }
            }
          );
          toast.success('Ученик успешно создан');
        } else {
          await axios.put(
            `${API_URL}/student/${editId}`,
            {
              FullName: newStudent.name,
              ClassName: newStudent.class
            },
            {
              headers: {
                Authorization: `Bearer ${localStorage.getItem('authToken')}`,
                'Content-Type': 'application/json'
              }
            }
          );
          toast.success('Данные ученика успешно обновлены');
        }
        await fetchStudents(pagination.students.page);
      }

      else if (modalType === 'grades' || modalType === 'edit_grades') {
        if (!newGrades.student || !newGrades.subject1 || !newGrades.subject2 || !newGrades.subject3) {
          toast.error('Заполните все поля для оценок');
          return;
        }

        if (modalType === 'grades') {
          await axios.post(
            `${API_URL}/grade`,
            {
              StudentName: newGrades.student,
              Subject1Grade: newGrades.subject1,
              Subject2Grade: newGrades.subject2,
              Subject3Grade: newGrades.subject3
            },
            {
              headers: {
                Authorization: `Bearer ${localStorage.getItem('authToken')}`,
                'Content-Type': 'application/json'
              }
            }
          );
          toast.success('Оценки успешно добавлены');
        } else {
          await axios.put(
            `${API_URL}/grade/${editId}`,
            {
              StudentName: newGrades.student,
              Subject1Grade: newGrades.subject1,
              Subject2Grade: newGrades.subject2,
              Subject3Grade: newGrades.subject3
            },
            {
              headers: {
                Authorization: `Bearer ${localStorage.getItem('authToken')}`,
                'Content-Type': 'application/json'
              }
            }
          );
          toast.success('Оценки успешно обновлены');
        }
        await fetchGrades(pagination.grades.page);
      }

      setShowAddModal(false);
      resetForm();
    } catch (err) {
      toast.error(err.response?.data?.message || 'Ошибка при сохранении данных');
      setError(err.response?.data?.details || err.message);
    } finally {
      setIsLoading(false);
    }
  };

  // Обработчики действий
  const handleAdd = (type) => {
    setModalType(type);
    setEditId(null);
    setShowAddModal(true);
    setError(null);
    resetForm();
  };

  const handleEdit = (type, id) => {
    let data;
    if (type === 'user') {
      data = users.find(u => u.UserID === id);
      setNewUser({
        username: data.Username,
        password: '',
        confirmPassword: '',
        role: data.Role
      });
    } else if (type === 'teacher') {
      data = teachers.find(t => t.id === id);
      setNewTeacher({
        name: data.fullName,
        subject: data.subject,
        room: data.room,
        userId: data.UserID
      });
    } else if (type === 'student') {
      data = students.find(s => s.id === id);
      setNewStudent({
        name: data.fullName,
        class: data.className
      });
    } else {
      data = grades.find(g => g.id === id);
      setNewGrades({
        student: data.studentName,
        subject1: data.subject1Grade,
        subject2: data.subject2Grade,
        subject3: data.subject3Grade
      });
    }

    setModalType(`edit_${type}`);
    setEditId(id);
    setShowAddModal(true);
  };

  const handleDelete = async (id, type) => {
    try {
      setIsLoading(true);
      if (!window.confirm(`Вы уверены, что хотите удалить этот ${type === 'teacher' ? 'учителя' : type === 'student' ? 'ученика' : type === 'user' ? 'пользователя' : 'оценку'}?`)) {
        return;
      }

      await axios.delete(`${API_URL}/${type}/${id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` }
      });

      toast.success(`Успешно удалено!`);

      if (type === 'teacher') await fetchTeachers(pagination.teachers.page);
      else if (type === 'student') await fetchStudents(pagination.students.page);
      else if (type === 'user') await fetchUsers(pagination.users.page);
      else await fetchGrades(pagination.grades.page);
    } catch (err) {
      toast.error(err.response?.data?.message || 'Ошибка при удалении');
    } finally {
      setIsLoading(false);
    }
  };

  const handleLogout = () => {
    setShowLogoutModal(true);
  };

  const confirmLogout = () => {
    localStorage.removeItem('authToken');
    navigate('/');
    toast.success('Вы успешно вышли из системы');
  };

  const cancelLogout = () => {
    setShowLogoutModal(false);
  };

  return (
    <div className="admin-dashboard">
      <ToastContainer
        position="top-right"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
      />
      
      {isLoading && <div className="loading-overlay">Загрузка...</div>}
      
      <header className="dashboard-header">
        <h1>Панель администратора</h1>
        <div className="header-controls">
          <button 
            className="back-btn"
            onClick={handleLogout}
            disabled={isLoading}
          >
            <i className="fas fa-arrow-left"></i> Выйти
          </button>
          <div className="search-box">
            <input
              type="text"
              placeholder="Поиск..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <i className="fas fa-search"></i>
          </div>
          <button 
            className="refresh-btn"
            onClick={() => {
              if (activeTab === 'teachers') fetchTeachers(pagination.teachers.page);
              else if (activeTab === 'students') fetchStudents(pagination.students.page);
              else if (activeTab === 'grades') fetchGrades(pagination.grades.page);
              else if (activeTab === 'users') fetchUsers(pagination.users.page);
              else fetchStats();
            }}
            disabled={isLoading}
          >
            <i className="fas fa-sync-alt"></i> Обновить
          </button>
        </div>
      </header>

      <div className="dashboard-container">
        <nav className="sidebar">
          <ul>
            <li 
              className={activeTab === 'teachers' ? 'active' : ''}
              onClick={() => setActiveTab('teachers')}
            >
              <i className="fas fa-chalkboard-teacher"></i> Учителя
            </li>
            <li 
              className={activeTab === 'students' ? 'active' : ''}
              onClick={() => setActiveTab('students')}
            >
              <i className="fas fa-user-graduate"></i> Ученики
            </li>
            <li 
              className={activeTab === 'grades' ? 'active' : ''}
              onClick={() => setActiveTab('grades')}
            >
              <i className="fas fa-clipboard-list"></i> Успеваемость
            </li>
            <li 
              className={activeTab === 'stats' ? 'active' : ''}
              onClick={() => setActiveTab('stats')}
            >
              <i className="fas fa-chart-bar"></i> Статистика
            </li>
            <li 
              className={activeTab === 'users' ? 'active' : ''}
              onClick={() => setActiveTab('users')}
            >
              <i className="fas fa-users"></i> Пользователи
            </li>
          </ul>
        </nav>

        <main className="content">
          {activeTab === 'teachers' && (
            <div className="table-section">
              <div className="section-header">
                <h2>Список учителей</h2>
                <div className="section-actions">
                  <button 
                    className="export-btn"
                    onClick={() => exportData('teachers')}
                    disabled={isLoading}
                  >
                    <i className="fas fa-file-export"></i> Экспорт
                  </button>
                  <button 
                    className="add-btn" 
                    onClick={() => handleAdd('teacher')}
                    disabled={isLoading}
                  >
                    <i className="fas fa-plus"></i> Добавить
                  </button>
                </div>
              </div>
              
              <div className="filters">
                <input
                  type="text"
                  placeholder="Фильтр по предмету"
                  value={filters.teachers.subject}
                  onChange={(e) => setFilters({
                    ...filters, 
                    teachers: {
                      ...filters.teachers, 
                      subject: e.target.value 
                    }
                  })}
                />
                <input
                  type="text"
                  placeholder="Фильтр по кабинету"
                  value={filters.teachers.room}
                  onChange={(e) => setFilters({
                    ...filters, 
                    teachers: {
                      ...filters.teachers, 
                      room: e.target.value 
                    }
                  })}
                />
              </div>
              
              <div className="table-container">
                <table>
                  <thead>
                    <tr>
                      <th>ID</th>
                      <th>ФИО</th>
                      <th>Предмет</th>
                      <th>Кабинет</th>
                      <th>Действия</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredData(teachers, 'teachers').map(teacher => (
                      <tr key={teacher.id}>
                        <td>{teacher.id}</td>
                        <td>{teacher.fullName}</td>
                        <td>{teacher.subject}</td>
                        <td>{teacher.room}</td>
                        <td className="actions">
                          <button 
                            className="edit-btn"
                            onClick={() => handleEdit('teacher', teacher.id)}
                            disabled={isLoading}
                          >
                            <i className="fas fa-edit"></i>
                          </button>
                          <button 
                            className="delete-btn"
                            onClick={() => handleDelete(teacher.id, 'teacher')}
                            disabled={isLoading}
                          >
                            <i className="fas fa-trash"></i>
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
              
              <div className="pagination">
                <button 
                  onClick={() => fetchTeachers(pagination.teachers.page - 1)}
                  disabled={pagination.teachers.page <= 1 || isLoading}
                >
                  Назад
                </button>
                <span>
                  Страница {pagination.teachers.page} из {' '}
                  {Math.ceil(pagination.teachers.total / pagination.teachers.perPage)}
                </span>
                <button 
                  onClick={() => fetchTeachers(pagination.teachers.page + 1)}
                  disabled={
                    pagination.teachers.page * pagination.teachers.perPage >= 
                    pagination.teachers.total || isLoading
                  }
                >
                  Вперед
                </button>
              </div>
            </div>
          )}

          {activeTab === 'students' && (
            <div className="table-section">
              <div className="section-header">
                <h2>Список учеников</h2>
                <div className="section-actions">
                  <button 
                    className="export-btn"
                    onClick={() => exportData('students')}
                    disabled={isLoading}
                  >
                    <i className="fas fa-file-export"></i> Экспорт
                  </button>
                  <button 
                    className="add-btn" 
                    onClick={() => handleAdd('student')}
                    disabled={isLoading}
                  >
                    <i className="fas fa-plus"></i> Добавить
                  </button>
                </div>
              </div>
              
              <div className="filters">
                <input
                  type="text"
                  placeholder="Фильтр по классу"
                  value={filters.students.class}
                  onChange={(e) => setFilters({
                    ...filters, 
                    students: {
                      ...filters.students, 
                      class: e.target.value 
                    }
                  })}
                />
              </div>
              
              <div className="table-container">
                <table>
                  <thead>
                    <tr>
                      <th>ID</th>
                      <th>ФИО</th>
                      <th>Класс</th>
                      <th>Действия</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredData(students, 'students').map(student => (
                      <tr key={student.id}>
                        <td>{student.id}</td>
                        <td>{student.fullName}</td>
                        <td>{student.className}</td>
                        <td className="actions">
                          <button 
                            className="edit-btn"
                            onClick={() => handleEdit('student', student.id)}
                            disabled={isLoading}
                          >
                            <i className="fas fa-edit"></i>
                          </button>
                          <button 
                            className="delete-btn"
                            onClick={() => handleDelete(student.id, 'student')}
                            disabled={isLoading}
                          >
                            <i className="fas fa-trash"></i>
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
              
              <div className="pagination">
                <button 
                  onClick={() => fetchStudents(pagination.students.page - 1)}
                  disabled={pagination.students.page <= 1 || isLoading}
                >
                  Назад
                </button>
                <span>
                  Страница {pagination.students.page} из {' '}
                  {Math.ceil(pagination.students.total / pagination.students.perPage)}
                </span>
                <button 
                  onClick={() => fetchStudents(pagination.students.page + 1)}
                  disabled={
                    pagination.students.page * pagination.students.perPage >= 
                    pagination.students.total || isLoading
                  }
                >
                  Вперед
                </button>
              </div>
            </div>
          )}

          {activeTab === 'grades' && (
            <div className="table-section">
              <div className="section-header">
                <h2>Успеваемость</h2>
                <div className="section-actions">
                  <button 
                    className="export-btn"
                    onClick={() => exportData('grades')}
                    disabled={isLoading}
                  >
                    <i className="fas fa-file-export"></i> Экспорт
                  </button>
                  <button 
                    className="add-btn" 
                    onClick={() => handleAdd('grades')}
                    disabled={isLoading}
                  >
                    <i className="fas fa-plus"></i> Добавить
                  </button>
                </div>
              </div>
              
              <div className="filters">
                <input
                  type="text"
                  placeholder="Фильтр по предмету"
                  value={filters.grades.subject}
                  onChange={(e) => setFilters({
                    ...filters, 
                    grades: {
                      ...filters.grades, 
                      subject: e.target.value 
                    }
                  })}
                />
                <input
                  type="number"
                  placeholder="Мин. оценка"
                  min="2"
                  max="5"
                  value={filters.grades.minGrade}
                  onChange={(e) => setFilters({
                    ...filters, 
                    grades: {
                      ...filters.grades, 
                      minGrade: e.target.value 
                    }
                  })}
                />
                <input
                  type="number"
                  placeholder="Макс. оценка"
                  min="2"
                  max="5"
                  value={filters.grades.maxGrade}
                  onChange={(e) => setFilters({
                    ...filters, 
                    grades: {
                      ...filters.grades, 
                      maxGrade: e.target.value 
                    }
                  })}
                />
              </div>
              
              <div className="table-container">
                <table>
                  <thead>
                    <tr>
                      <th>ID</th>
                      <th>Ученик</th>
                      <th>Математика</th>
                      <th>Физика</th>
                      <th>Литература</th>
                      <th>Действия</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredData(grades, 'grades').map(grade => (
                      <tr key={grade.id}>
                        <td>{grade.id}</td>
                        <td>{grade.studentName}</td>
                        <td className={grade.subject1Grade < 3 ? 'bad-grade' : ''}>
                          {grade.subject1Grade}
                        </td>
                        <td className={grade.subject2Grade < 3 ? 'bad-grade' : ''}>
                          {grade.subject2Grade}
                        </td>
                        <td className={grade.subject3Grade < 3 ? 'bad-grade' : ''}>
                          {grade.subject3Grade}
                        </td>
                        <td className="actions">
                          <button 
                            className="edit-btn"
                            onClick={() => handleEdit('grades', grade.id)}
                            disabled={isLoading}
                          >
                            <i className="fas fa-edit"></i>
                          </button>
                          <button 
                            className="delete-btn"
                            onClick={() => handleDelete(grade.id, 'grade')}
                            disabled={isLoading}
                          >
                            <i className="fas fa-trash"></i>
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
              
              <div className="pagination">
                <button 
                  onClick={() => fetchGrades(pagination.grades.page - 1)}
                  disabled={pagination.grades.page <= 1 || isLoading}
                >
                  Назад
                </button>
                <span>
                  Страница {pagination.grades.page} из {' '}
                  {Math.ceil(pagination.grades.total / pagination.grades.perPage)}
                </span>
                <button 
                  onClick={() => fetchGrades(pagination.grades.page + 1)}
                  disabled={
                    pagination.grades.page * pagination.grades.perPage >= 
                    pagination.grades.total || isLoading
                  }
                >
                  Вперед
                </button>
              </div>
            </div>
          )}

          {activeTab === 'stats' && (
            <div className="stats-section">
              <div className="section-header">
                <h2>Статистика успеваемости</h2>
                <button 
                  className="refresh-btn"
                  onClick={fetchStats}
                  disabled={isLoading}
                >
                  <i className="fas fa-sync-alt"></i> Обновить
                </button>
              </div>
              
              <div className="stats-grid">
                <div className="stat-card">
                  <div className="stat-icon bg-red">
                    <i className="fas fa-exclamation-triangle"></i>
                  </div>
                  <div className="stat-info">
                    <h3>Самый сложный предмет</h3>
                    <p>{stats.worstSubject || 'Нет данных'}</p>
                  </div>
                </div>

                <div className="stat-card">
                  <div className="stat-icon bg-orange">
                    <i className="fas fa-user-times"></i>
                  </div>
                  <div className="stat-info">
                    <h3>Неуспевающие ученики</h3>
                    <p>{stats.failingStudents}</p>
                  </div>
                </div>

                <div className="stat-card">
                  <div className="stat-icon bg-purple">
                    <i className="fas fa-chalkboard-teacher"></i>
                  </div>
                  <div className="stat-info">
                    <h3>Самый строгий учитель</h3>
                    <p>{stats.worstTeacher || 'Нет данных'}</p>
                  </div>
                </div>

                <div className="stat-card">
                  <div className="stat-icon bg-blue">
                    <i className="fas fa-users"></i>
                  </div>
                  <div className="stat-info">
                    <h3>Средние оценки по классам</h3>
                    <ul>
                      {Object.entries(stats.classAverages).map(([cls, avg]) => (
                        <li key={cls}>
                          {cls}: {typeof avg === 'number' ? avg.toFixed(1) : 'Нет данных'}
                        </li>
                      ))}
                    </ul>
                  </div>
                </div>

                <div className="stat-card">
                  <div className="stat-icon bg-green">
                    <i className="fas fa-trophy"></i>
                  </div>
                  <div className="stat-info">
                    <h3>Лучший класс</h3>
                    <p>{stats.bestClass || 'Нет данных'}</p>
                  </div>
                </div>

                <div className="stat-card">
                  <div className="stat-icon bg-red">
                    <i className="fas fa-sad-tear"></i>
                  </div>
                  <div className="stat-info">
                    <h3>Худший класс</h3>
                    <p>{stats.worstClass || 'Нет данных'}</p>
                  </div>
                </div>
              </div>
            </div>
          )}

          {activeTab === 'users' && (
            <div className="table-section">
              <div className="section-header">
                <h2>Список пользователей</h2>
                <div className="section-actions">
                  <button 
                    className="export-btn"
                    onClick={() => exportData('users')}
                    disabled={isLoading}
                  >
                    <i className="fas fa-file-export"></i> Экспорт
                  </button>
                  <button 
                    className="add-btn" 
                    onClick={() => handleAdd('user')}
                    disabled={isLoading}
                  >
                    <i className="fas fa-plus"></i> Добавить
                  </button>
                </div>
              </div>
              
              <div className="filters">
                <select
                  value={filters.users.role}
                  onChange={(e) => setFilters({
                    ...filters, 
                    users: {
                      ...filters.users, 
                      role: e.target.value 
                    }
                  })}
                >
                  <option value="">Все роли</option>
                  <option value="Admin">Администратор</option>
                  <option value="Teacher">Учитель</option>
                  <option value="Student">Ученик</option>
                  <option value="User">Пользователь</option>
                </select>
              </div>
              
              <div className="table-container">
                <table>
                  <thead>
                    <tr>
                      <th>ID</th>
                      <th>Логин</th>
                      <th>Роль</th>
                      <th>Действия</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredData(users, 'users').map(user => (
                      <tr key={user.UserID}>
                        <td>{user.UserID}</td>
                        <td>{user.Username}</td>
                        <td>{user.Role}</td>
                        <td className="actions">
                          <button 
                            className="edit-btn"
                            onClick={() => handleEdit('user', user.UserID)}
                            disabled={isLoading || user.Role === 'Admin'}
                          >
                            <i className="fas fa-edit"></i>
                          </button>
                          <button 
                            className="delete-btn"
                            onClick={() => handleDelete(user.UserID, 'user')}
                            disabled={isLoading || user.Role === 'Admin'}
                          >
                            <i className="fas fa-trash"></i>
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
              
              <div className="pagination">
                <button 
                  onClick={() => fetchUsers(pagination.users.page - 1)}
                  disabled={pagination.users.page <= 1 || isLoading}
                >
                  Назад
                </button>
                <span>
                  Страница {pagination.users.page} из {' '}
                  {Math.ceil(pagination.users.total / pagination.users.perPage)}
                </span>
                <button 
                  onClick={() => fetchUsers(pagination.users.page + 1)}
                  disabled={
                    pagination.users.page * pagination.users.perPage >= 
                    pagination.users.total || isLoading
                  }
                >
                  Вперед
                </button>
              </div>
            </div>
          )}
        </main>
      </div>
    </div>
  );
};

export default AdminDashboard;
