              <button 
                className="cancel-btn"
                onClick={cancelLogout}
              >
                Остаться
              </button>
              <button 
                className="confirm-btn"
                onClick={confirmLogout}
              >
                Да, выйти
              </button>
            </div>
          </div>
        </div>
      )}

