                  <div className="form-group">
                    <label>Кабинет</label>
                    <input 
                      type="number" 
                      placeholder="Номер кабинета"
                      value={classroomNumber}
                      onChange={(e) => setClassroomNumber(e.target.value)}
                      disabled={isLoading}
                    />
                  </div>
                </>
              )}

              {(modalType === 'student' || modalType === 'edit_student') && (
                <>
                  <div className="form-group">
                    <label>Пользователь</label>
                    <select
                      value={newStudent.userId}
                      onChange={e => setNewStudent({ 
                        ...newStudent, 
                        userId: +e.target.value,
                        userQuery: users.find(u => u.UserID === +e.target.value)?.Username || ''
                      })}
                      required
                      disabled={isLoading}
                    >
                      <option value="">— Выберите пользователя —</option>
                      {users
                        .filter(u => u.Role === 'Student')
                        .map(u => (
                          <option key={u.UserID} value={u.UserID}>
                            {u.Username} (ID={u.UserID})
                          </option>
                        ))
                      }
                    </select>
                  </div>
                  <div className="form-group">
                    <label>ФИО</label>
                    <input
                      type="text"
                      placeholder="Полное имя"
                      value={newStudent.name}
                      onChange={e => setNewStudent({ ...newStudent, name: e.target.value })}
                      required
                      disabled={isLoading}
                    />
                  </div>
                  <div className="form-group">
                    <label>Класс</label>
                    <select
                      value={newStudent.class}
                      onChange={e => setNewStudent({ ...newStudent, class: e.target.value })}
                      required
                      disabled={isLoading}
                    >
                      <option value="">Выберите класс</option>
                      {classrooms.map(cls => (
                        <option key={cls.ClassID} value={cls.Name}>
                          {cls.Name} ({cls.AcademicYear})
                        </option>
                      ))}
                    </select>
                  </div>
                </>
              )}

              {(modalType === 'grades' || modalType === 'edit_grades') && (
                <>
                  <div className="form-group">
                    <label>Ученик</label>
                    <select
                      value={newGrades.student}
                      onChange={(e) => setNewGrades({
                        ...newGrades, 
                        student: e.target.value
                      })}
                      required
                      disabled={isLoading}
                    >
                      <option value="">Выберите ученика</option>
                      {students.map(student => (
                        <option key={student.id} value={student.fullName}>
                          {student.fullName} ({student.className})
