import React, { useState } from "react";
import 'animate.css';
import "./App.css";

function App() {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [isContactOpen, setIsContactOpen] = useState(false);

  const toggleDropdown = () => setIsDropdownOpen(!isDropdownOpen);
  const toggleContact = () => setIsContactOpen(!isContactOpen);

  return (
    <div className="App">
      <header className="header">
        <div className="logo">
          <h1 className="animate__animated animate__fadeInDown">Школьный Дневник</h1>
        </div>
        <div className="auth-btns">
          <button className="btn" onClick={() => alert("Авторизация")}>Авторизация</button>
          <button className="btn" onClick={() => alert("Регистрация")}>Регистрация</button>
        </div>
      </header>

      <main className="main-content">
        <section className="intro animate__animated animate__fadeInUp">
          <h2>Добро пожаловать в школьный дневник</h2>
          <p>Здесь вы найдете информацию о вашем учебном процессе, отметки, расписание и многое другое.</p>
        </section>

        <section className="about">
          <button className="accordion" onClick={toggleDropdown}>
            О приложении
          </button>
          {isDropdownOpen && (
            <div className="panel">
              <p>Школьный дневник помогает учителям и ученикам отслеживать успеваемость, оценки, расписания и многое другое...</p>
            </div>
          )}
        </section>

        <section className="contact">
          <button className="accordion" onClick={toggleContact}>
            Контакты
          </button>
          {isContactOpen && (
            <div className="panel">
              <p>Наши контакты: <br /> email@school.com <br /> +1234567890</p>
            </div>
          )}
        </section>
      </main>

      <footer className="footer">
        <p>&copy; 2025 Школьный Дневник</p>
      </footer>
    </div>
  );
}

export default App;
