import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';

import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const API_URL = "http://localhost:5000/api";
  const navigate = useNavigate();

  // Состояния для управления интерфейсом
  const [activeTab, setActiveTab] = useState('teachers');
  const [showAddModal, setShowAddModal] = useState(false);
  const [showLogoutModal, setShowLogoutModal] = useState(false);
  const [modalType, setModalType] = useState('');
  const [editId, setEditId] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  // Основные данные
  const [teachers, setTeachers] = useState([]);
  const [students, setStudents] = useState([]);
  const [grades, setGrades] = useState([]);
  const [users, setUsers] = useState([]);
  const [stats, setStats] = useState({
    worstSubject: '',
    failingStudents: 0,
    worstTeacher: '',
    classAverages: {},
    bestClass: '',
    worstClass: ''
  });

  // Состояния для форм
  const [selectedUserId, setSelectedUserId] = useState('');
  const [fullName, setFullName] = useState('');
  const [classroomNumber, setClassroomNumber] = useState('');
  const [newStudent, setNewStudent] = useState({ 
    name: '', 
    class: '' 
  });
  const [newGrades, setNewGrades] = useState({ 
    student: '', 
    subject1: '', 
    subject2: '', 
    subject3: '' 
  });
  const [newUser, setNewUser] = useState({
    username: '',
    password: '',
    confirmPassword: '',
    role: 'User'
  });

  // Пагинация
  const [pagination, setPagination] = useState({
    teachers: { page: 1, perPage: 10, total: 0 },
    students: { page: 1, perPage: 10, total: 0 },
    grades: { page: 1, perPage: 10, total: 0 },
    users: { page: 1, perPage: 10, total: 0 }
  });

  // Фильтры
  const [filters, setFilters] = useState({
    teachers: { room: '' },
    students: { class: '' },
    grades: { subject: '', minGrade: '', maxGrade: '' },
    users: { role: '' }
  });

  // Проверка авторизации и роли
  useEffect(() => {
    const token = localStorage.getItem('authToken');
    if (!token) {
      navigate('/');
      toast.warning('Доступ запрещен. Требуется вход в систему.');
      return;
    }
    const { role } = jwtDecode(token);
    if (role !== 'Admin') {
      navigate('/');
      toast.warning('Доступ запрещен. Требуются права администратора.');
    }
  }, [navigate]);

  // Загрузка данных при монтировании и смене вкладки
  useEffect(() => {
    const loadData = async () => {
      try {
        setIsLoading(true);
        switch (activeTab) {
          case 'teachers':
            await Promise.all([
              fetchTeachers(),
              fetchUsers(pagination.users.page, 'Teacher')
            ]);
            break;
          case 'students':
            await fetchStudents();
            break;
          case 'grades':
            await fetchGrades();
            break;
          case 'stats':
            await fetchStats();
            break;
          case 'users':
            await fetchUsers();
            break;
          default:
            break;
        }
      } catch (err) {
        setError(err.message);
        toast.error(`Ошибка загрузки данных: ${err.message}`);
      } finally {
        setIsLoading(false);
      }
    };

    loadData();
  }, [activeTab]);

  // API функции
  const fetchTeachers = async (page = 1) => {
    try {
      const response = await axios.get(`${API_URL}/teacher`, {
        params: { 
          page, 
          limit: pagination.teachers.perPage,
          search: searchTerm,
          ...filters.teachers
        },
        headers: { 
          Authorization: `Bearer ${localStorage.getItem('authToken')}` 
        }
      });
      // Ожидаем { teachers: [{ TeacherID, UserID, FullName, ClassroomNumber }], totalCount: N }
      setTeachers(response.data.teachers);
      setPagination(prev => ({
        ...prev,
        teachers: {
          ...prev.teachers,
          page,
          total: response.data.totalCount
        }
      }));
    } catch (err) {
      toast.error('Ошибка загрузки учителей');
      throw err;
    }
  };

  const fetchStudents = async (page = 1) => {
    try {
      const response = await axios.get(`${API_URL}/student`, {
        params: { 
          page, 
          limit: pagination.students.perPage,
          search: searchTerm,
          ...filters.students
        },
        headers: { 
          Authorization: `Bearer ${localStorage.getItem('authToken')}` 
        }
      });
      setStudents(response.data.students);
      setPagination(prev => ({
        ...prev,
        students: {
          ...prev.students,
          page,
          total: response.data.totalCount
        }
      }));
    } catch (err) {
      toast.error('Ошибка загрузки студентов');
      throw err;
    }
  };

  const fetchGrades = async (page = 1) => {
    try {
      const response = await axios.get(`${API_URL}/grade`, {
        params: { 
          page, 
          limit: pagination.grades.perPage,
          search: searchTerm,
          ...filters.grades
        },
        headers: { 
          Authorization: `Bearer ${localStorage.getItem('authToken')}` 
        }
      });
      setGrades(response.data.grades);
      setPagination(prev => ({
        ...prev,
        grades: {
          ...prev.grades,
          page,
          total: response.data.totalCount
        }
      }));
    } catch (err) {
      toast.error('Ошибка загрузки оценок');
      throw err;
    }
  };

  const fetchUsers = async (page = 1) => {
    const res = await axios.get(`${API_URL}/user`, {
      params: {
        page,
        limit: pagination.users.perPage,
        search: searchTerm,
        role: filters.users.role
      },
      headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` }
    });
  
    setUsers(res.data);
    
    const totalHeader = res.headers['x-total-count'] || res.headers['X-Total-Count'];
const totalCount = parseInt(totalHeader, 10);
console.log('res.headers:', res.headers);

setPagination(prev => ({
  ...prev,
  users: {
    ...prev.users,
    page,
    total: isNaN(totalCount) ? 0 : totalCount
  }
}));
  }
  
  

  const fetchStats = async () => {
    try {
      const [failing, worstTeacher, classStats] = await Promise.all([
        axios.get(`${API_URL}/report/failing`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` }
        }),
        axios.get(`${API_URL}/report/worst-teacher`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` }
        }),
        axios.get(`${API_URL}/report/class`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` }
        })
      ]);

      setStats({
        worstSubject: 'Физика',
        failingStudents: failing.data.count || 0,
        worstTeacher: worstTeacher.data[0]?.name || 'Нет данных',
        classAverages: classStats.data.reduce((acc, curr) => {
          acc[curr.className] = curr.average;
          return acc;
        }, {}),
        bestClass: classStats.data.length > 0 
          ? classStats.data.reduce((prev, current) => 
              (prev.average > current.average) ? prev : current
            ).className 
          : 'Нет данных',
        worstClass: classStats.data.length > 0 
          ? classStats.data.reduce((prev, current) => 
              (prev.average < current.average) ? prev : current
            ).className 
          : 'Нет данных'
      });
    } catch (err) {
      toast.error('Ошибка загрузки статистики');
      throw err;
    }
  };

  // Функция для добавления/редактирования
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setIsLoading(true);
      setError(null);

      if (modalType === 'teacher') {
        if (!selectedUserId || !fullName.trim()) {
          toast.error('Выберите пользователя и введите полное имя');
          return;
        }

        const payload = {
          UserID: selectedUserId,
          FullName: fullName,
          ClassroomNumber: classroomNumber || null
        };

        await axios.post(
          `${API_URL}/teacher`,
          payload,
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem('authToken')}`,
              'Content-Type': 'application/json'
            }
          }
        );
        toast.success('Учитель успешно назначен');
        await fetchTeachers(pagination.teachers.page);
      }

      else if (modalType === 'edit_teacher') {
        if (!fullName.trim()) {
          toast.error('Введите полное имя');
          return;
        }
      
        // Проверка, чтобы UserID не был null
        if (!selectedUserId) {
          toast.error('Выберите пользователя');
          return;
        }
      
        const payload = {
          FullName: fullName,
          ClassroomNumber: classroomNumber || null,
          UserID: selectedUserId // убедитесь, что UserID передается в запрос
        };
      
        await axios.put(
          `${API_URL}/teacher/${editId}`,
          payload,
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem('authToken')}`,
              'Content-Type': 'application/json'
            }
          }
        );
        toast.success('Данные учителя успешно обновлены');
        await fetchTeachers(pagination.teachers.page);
      }

      else if (modalType === 'student' || modalType === 'edit_student') {
        if (!newStudent.name.trim() || !newStudent.class) {
          toast.error('Заполните все поля для ученика');
          return;
        }

        if (modalType === 'student') {
          await axios.post(
            `${API_URL}/student`,
            {
              FullName: newStudent.name,
              ClassName: newStudent.class
            },
            {
              headers: {
                Authorization: `Bearer ${localStorage.getItem('authToken')}`,
                'Content-Type': 'application/json'
              }
            }
          );
          toast.success('Ученик успешно создан');
        } else {
          await axios.put(
            `${API_URL}/student/${editId}`,
            {
              FullName: newStudent.name,
              ClassName: newStudent.class
            },
            {
              headers: {
                Authorization: `Bearer ${localStorage.getItem('authToken')}`,
                'Content-Type': 'application/json'
              }
            }
          );
          toast.success('Данные ученика успешно обновлены');
        }
        await fetchStudents(pagination.students.page);
      }

      else if (modalType === 'grades' || modalType === 'edit_grades') {
        if (!newGrades.student || !newGrades.subject1 || !newGrades.subject2 || !newGrades.subject3) {
          toast.error('Заполните все поля для оценок');
          return;
        }

        if (modalType === 'grades') {
          await axios.post(
            `${API_URL}/grade`,
            {
              StudentName: newGrades.student,
              Subject1Grade: newGrades.subject1,
              Subject2Grade: newGrades.subject2,
              Subject3Grade: newGrades.subject3
            },
            {
              headers: {
                Authorization: `Bearer ${localStorage.getItem('authToken')}`,
                'Content-Type': 'application/json'
              }
            }
          );
          toast.success('Оценки успешно добавлены');
        } else {
          await axios.put(
            `${API_URL}/grade/${editId}`,
            {
              StudentName: newGrades.student,
              Subject1Grade: newGrades.subject1,
              Subject2Grade: newGrades.subject2,
              Subject3Grade: newGrades.subject3
            },
            {
              headers: {
                Authorization: `Bearer ${localStorage.getItem('authToken')}`,
                'Content-Type': 'application/json'
              }
            }
          );
          toast.success('Оценки успешно обновлены');
        }
        await fetchGrades(pagination.grades.page);
      }

      setShowAddModal(false);
      resetForm();
    } catch (err) {
      toast.error(err.response?.data?.message || 'Ошибка при сохранении данных');
      setError(err.response?.data?.details || err.message);
    } finally {
      setIsLoading(false);
    }
  };

  // Обработчики действий
  const handleAdd = (type) => {
    setModalType(type);
    setEditId(null);
    setShowAddModal(true);
    setError(null);
    resetForm();
  };

  const handleEdit = (type, id) => {
    let data;
    if (type === 'teacher') {
      // Найдем учителя по его ID
      data = teachers.find(t => t.TeacherID === id);
      
      // Устанавливаем данные для редактирования
      setFullName(data.FullName);
      setClassroomNumber(data.ClassroomNumber || '');
      setSelectedUserId(data.UserID);  // Устанавливаем UserID
    }
  
    setModalType(`edit_${type}`);
    setEditId(id);  // Устанавливаем ID редактируемого объекта
    setShowAddModal(true);  // Открываем модальное окно
  };
  
  

  const handleDelete = async (id, type) => {
    try {
      setIsLoading(true);
      if (!window.confirm(`Вы уверены, что хотите удалить этого ${type === 'teacher' ? 'учителя' : type === 'student' ? 'ученика' : type === 'user' ? 'пользователя' : 'оценку'}?`)) {
        return;
      }
  
      await axios.delete(`${API_URL}/${type}/${id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` }
      });
  
      toast.success(`Успешно удалено!`);
      
      if (type === 'teacher') await fetchTeachers(pagination.teachers.page);
      else if (type === 'student') await fetchStudents(pagination.students.page);
      else if (type === 'user') await fetchUsers(pagination.users.page);
      else await fetchGrades(pagination.grades.page);
    } catch (err) {
      toast.error(err.response?.data?.message || 'Ошибка при удалении');
    } finally {
      setIsLoading(false);
    }
  };
  

  const handleUserSubmit = async (e) => {
    e.preventDefault();
    try {
      setIsLoading(true);
      
      if (!newUser.username || !newUser.role || (modalType === 'user' && (!newUser.password || !newUser.confirmPassword))) {
        toast.error('Заполните все обязательные поля');
        return;
      }

      if (modalType === 'user' && newUser.password !== newUser.confirmPassword) {
        toast.error('Пароли не совпадают');
        return;
      }

      if (modalType === 'user' && newUser.password.length < 6) {
        toast.error('Пароль должен быть не менее 6 символов');
        return;
      }

      if (modalType === 'edit_user') {
        await axios.put(
          `${API_URL}/user/${editId}/role`,
          { role: newUser.role },
          {
            headers: { 
              Authorization: `Bearer ${localStorage.getItem('authToken')}`,
              'Content-Type': 'application/json'
            }
          }
        );
        toast.success('Роль пользователя успешно обновлена');
      } else {
        await axios.post(
          `${API_URL}/user`,
          {
            username: newUser.username,
            password: newUser.password,
            role: newUser.role
          },
          {
            headers: { 
              Authorization: `Bearer ${localStorage.getItem('authToken')}`,
              'Content-Type': 'application/json'
            }
          }
        );
        toast.success('Пользователь успешно создан');
      }

      await fetchUsers(pagination.users.page);
      setShowAddModal(false);
      resetForm();
    } catch (err) {
      toast.error(err.response?.data?.message || 'Ошибка при сохранении пользователя');
    } finally {
      setIsLoading(false);
    }
  };

  const resetForm = () => {
    setSelectedUserId('');
    setFullName('');
    setClassroomNumber('');
    setNewStudent({ name: '', class: '' });
    setNewGrades({ student: '', subject1: '', subject2: '', subject3: '' });
    setNewUser({ username: '', password: '', confirmPassword: '', role: 'User' });
    setEditId(null);
  };

  const exportData = async (type) => {
    try {
      setIsLoading(true);
      const response = await axios.get(`${API_URL}/export/${type}`, {
        responseType: 'blob',
        headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` }
      });
      
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `${type}_export_${new Date().toISOString().split('T')[0]}.xlsx`);
      document.body.appendChild(link);
      link.click();
      link.remove();
      
      toast.success(`Данные ${type} успешно экспортированы`);
    } catch (err) {
      toast.error(`Ошибка экспорта: ${err.response?.data?.message || err.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  // Фильтрация данных для поиска
  const filteredData = (data, type) => {
    if (!data) return [];
    
    return data.filter(item => {
      const matchesSearch = 
        item.FullName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        item.className?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        item.studentName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        item.Username?.toLowerCase().includes(searchTerm.toLowerCase());
      
      const matchesFilters = 
        (type !== 'teachers' || (
          !filters.teachers.room || item.ClassroomNumber?.toString().includes(filters.teachers.room)
        )) &&
        (type !== 'students' || (
          !filters.students.class || item.className?.includes(filters.students.class)
        )) &&
        (type !== 'grades' || (
          (!filters.grades.subject || (
            item.subject1Name?.includes(filters.grades.subject) ||
            item.subject2Name?.includes(filters.grades.subject) ||
            item.subject3Name?.includes(filters.grades.subject)
          )) &&
          (!filters.grades.minGrade || (
            item.subject1Grade >= +filters.grades.minGrade &&
            item.subject2Grade >= +filters.grades.minGrade &&
            item.subject3Grade >= +filters.grades.minGrade
          )) &&
          (!filters.grades.maxGrade || (
            item.subject1Grade <= +filters.grades.maxGrade &&
            item.subject2Grade <= +filters.grades.maxGrade &&
            item.subject3Grade <= +filters.grades.maxGrade
          ))
        )) &&
        (type !== 'users' || (
          !filters.users.role || item.Role?.includes(filters.users.role)
        ));
      
      return matchesSearch && matchesFilters;
    });
  };

  // Функция для выхода
  const handleLogout = () => {
    setShowLogoutModal(true);
  };

  // Подтвержденный выход
  const confirmLogout = () => {
    localStorage.removeItem('authToken');
    navigate('/');
    toast.success('Вы успешно вышли из системы');
  };

  // Отмена выхода
  const cancelLogout = () => {
    setShowLogoutModal(false);
  };

  return (
    <div className="admin-dashboard">
      <ToastContainer
        position="top-right"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
      />
      
      {isLoading && <div className="loading-overlay">Загрузка...</div>}
      
      <header className="dashboard-header">
        <h1>Панель администратора</h1>
        <div className="header-controls">
          <button 
            className="back-btn"
            onClick={handleLogout}
            disabled={isLoading}
          >
            <i className="fas fa-arrow-left"></i> Выйти
          </button>
          <div className="search-box">
            <input
              type="text"
              placeholder="Поиск..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <i className="fas fa-search"></i>
          </div>
          <button 
            className="refresh-btn"
            onClick={() => {
              if (activeTab === 'teachers') {
                fetchTeachers(pagination.teachers.page);
                fetchUsers(pagination.users.page, 'Teacher');
              }
              else if (activeTab === 'students') fetchStudents(pagination.students.page);
              else if (activeTab === 'grades') fetchGrades(pagination.grades.page);
              else if (activeTab === 'users') fetchUsers(pagination.users.page);
              else fetchStats();
            }}
            disabled={isLoading}
          >
            <i className="fas fa-sync-alt"></i> Обновить
          </button>
        </div>
      </header>

      <div className="dashboard-container">
        <nav className="sidebar">
          <ul>
            <li 
              className={activeTab === 'teachers' ? 'active' : ''}
              onClick={() => setActiveTab('teachers')}
            >
              <i className="fas fa-chalkboard-teacher"></i> Учителя
            </li>
            <li 
              className={activeTab === 'students' ? 'active' : ''}
              onClick={() => setActiveTab('students')}
            >
              <i className="fas fa-user-graduate"></i> Ученики
            </li>
            <li 
              className={activeTab === 'grades' ? 'active' : ''}
              onClick={() => setActiveTab('grades')}
            >
              <i className="fas fa-clipboard-list"></i> Успеваемость
            </li>
            <li 
              className={activeTab === 'stats' ? 'active' : ''}
              onClick={() => setActiveTab('stats')}
            >
              <i className="fas fa-chart-bar"></i> Статистика
            </li>
            <li 
              className={activeTab === 'users' ? 'active' : ''}
              onClick={() => setActiveTab('users')}
            >
              <i className="fas fa-users"></i> Пользователи
            </li>
          </ul>
        </nav>

        <main className="content">
          {activeTab === 'teachers' && (
            <div className="table-section">
              <div className="section-header">
                <h2>Список учителей</h2>
                <div className="section-actions">
                  <button 
                    className="export-btn"
                    onClick={() => exportData('teachers')}
                    disabled={isLoading}
                  >
                    <i className="fas fa-file-export"></i> Экспорт
                  </button>
                  <button 
                    className="add-btn" 
                    onClick={() => handleAdd('teacher')}
                    disabled={isLoading}
                  >
                    <i className="fas fa-plus"></i> Добавить
                  </button>
                </div>
              </div>
              
              <div className="filters">
                <input
                  type="text"
                  placeholder="Фильтр по кабинету"
                  value={filters.teachers.room}
                  onChange={(e) => setFilters({
                    ...filters, 
                    teachers: {
                      ...filters.teachers, 
                      room: e.target.value 
                    }
                  })}
                />
              </div>
              
              <div className="table-container">
                <table>
                  <thead>
                    <tr>
                      <th>TeacherID</th>
                      <th>UserID</th>
                      <th>ФИО</th>
                      <th>Кабинет</th>
                      <th>Действия</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredData(teachers, 'teachers').map(teacher => (
                      <tr key={teacher.TeacherID}>
                        <td>{teacher.TeacherID}</td>
                        <td>{teacher.UserID}</td>
                        <td>{teacher.FullName}</td>
                        <td>{teacher.ClassroomNumber}</td>
                        <td className="actions">
                          <button 
                            className="edit-btn"
                            onClick={() => handleEdit('teacher', teacher.TeacherID)}
                            disabled={isLoading}
                          >
                            <i className="fas fa-edit"></i> Редактировать
                          </button>
                          <button 
                            className="delete-btn"
                            onClick={() => handleDelete(teacher.TeacherID, 'teacher')}
                            disabled={isLoading}
                          >
                            <i className="fas fa-trash"></i> Удалить
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
              
              <div className="pagination">
                <button 
                  onClick={() => fetchTeachers(pagination.teachers.page - 1)}
                  disabled={pagination.teachers.page <= 1 || isLoading}
                >
                  Назад
                </button>
                <span>
  Страница {pagination.teachers.page} из {' '}
  {Math.ceil(pagination.teachers.total / pagination.teachers.perPage)}
</span>

                <button 
                  onClick={() => fetchTeachers(pagination.teachers.page + 1)}
                  disabled={
                    pagination.teachers.page * pagination.teachers.perPage >= 
                    pagination.teachers.total || isLoading
                  }
                >
                  Вперед
                </button>
              </div>
            </div>
          )}

          {activeTab === 'students' && (
            <div className="table-section">
              <div className="section-header">
                <h2>Список учеников</h2>
                <div className="section-actions">
                  <button 
                    className="export-btn"
                    onClick={() => exportData('students')}
                    disabled={isLoading}
                  >
                    <i className="fas fa-file-export"></i> Экспорт
                  </button>
                  <button 
                    className="add-btn" 
                    onClick={() => handleAdd('student')}
                    disabled={isLoading}
                  >
                    <i className="fas fa-plus"></i> Добавить
                  </button>
                </div>
              </div>
              
              <div className="filters">
                <input
                  type="text"
                  placeholder="Фильтр по классу"
                  value={filters.students.class}
                  onChange={(e) => setFilters({
                    ...filters, 
                    students: {
                      ...filters.students, 
                      class: e.target.value 
                    }
                  })}
                />
              </div>
              
              <div className="table-container">
                <table>
                  <thead>
                    <tr>
                      <th>ID</th>
                      <th>ФИО</th>
                      <th>Класс</th>
                      <th>Действия</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredData(students, 'students').map(student => (
                      <tr key={student.id}>
                        <td>{student.id}</td>
                        <td>{student.fullName}</td>
                        <td>{student.className}</td>
                        <td className="actions">
                          <button 
                            className="edit-btn"
                            onClick={() => handleEdit('student', student.id)}
                            disabled={isLoading}
                          >
                            <i className="fas fa-edit"></i>
                          </button>
                          <button 
                            className="delete-btn"
                            onClick={() => handleDelete(student.id, 'student')}
                            disabled={isLoading}
                          >
                            <i className="fas fa-trash"></i>
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
              
              <div className="pagination">
                <button 
                  onClick={() => fetchStudents(pagination.students.page - 1)}
                  disabled={pagination.students.page <= 1 || isLoading}
                >
                  Назад
                </button>
                <span>
                  Страница {pagination.students.page} из {' '}
                  {Math.ceil(pagination.students.total / pagination.students.perPage)}
                </span>
                <button 
                  onClick={() => fetchStudents(pagination.students.page + 1)}
                  disabled={
                    pagination.students.page * pagination.students.perPage >= 
                    pagination.students.total || isLoading
                  }
                >
                  Вперед
                </button>
              </div>
            </div>
          )}

          {activeTab === 'grades' && (
            <div className="table-section">
              <div className="section-header">
                <h2>Успеваемость</h2>
                <div className="section-actions">
                  <button 
                    className="export-btn"
                    onClick={() => exportData('grades')}
                    disabled={isLoading}
                  >
                    <i className="fas fa-file-export"></i> Экспорт
                  </button>
                  <button 
                    className="add-btn" 
                    onClick={() => handleAdd('grades')}
                    disabled={isLoading}
                  >
                    <i className="fas fa-plus"></i> Добавить
                  </button>
                </div>
              </div>
              
              <div className="filters">
                <input
                  type="text"
                  placeholder="Фильтр по предмету"
                  value={filters.grades.subject}
                  onChange={(e) => setFilters({
                    ...filters, 
                    grades: {
                      ...filters.grades, 
                      subject: e.target.value 
                    }
                  })}
                />
                <input
                  type="number"
                  placeholder="Мин. оценка"
                  min="2"
                  max="5"
                  value={filters.grades.minGrade}
                  onChange={(e) => setFilters({
                    ...filters, 
                    grades: {
                      ...filters.grades, 
                      minGrade: e.target.value 
                    }
                  })}
                />
                <input
                  type="number"
                  placeholder="Макс. оценка"
                  min="2"
                  max="5"
                  value={filters.grades.maxGrade}
                  onChange={(e) => setFilters({
                    ...filters, 
                    grades: {
                      ...filters.grades, 
                      maxGrade: e.target.value 
                    }
                  })}
                />
              </div>
              
              <div className="table-container">
                <table>
                  <thead>
                    <tr>
                      <th>ID</th>
                      <th>Ученик</th>
                      <th>Математика</th>
                      <th>Физика</th>
                      <th>Литература</th>
                      <th>Действия</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredData(grades, 'grades').map(grade => (
                      <tr key={grade.id}>
                        <td>{grade.id}</td>
                        <td>{grade.studentName}</td>
                        <td className={grade.subject1Grade < 3 ? 'bad-grade' : ''}>
                          {grade.subject1Grade}
                        </td>
                        <td className={grade.subject2Grade < 3 ? 'bad-grade' : ''}>
                          {grade.subject2Grade}
                        </td>
                        <td className={grade.subject3Grade < 3 ? 'bad-grade' : ''}>
                          {grade.subject3Grade}
                        </td>
                        <td className="actions">
                          <button 
                            className="edit-btn"
                            onClick={() => handleEdit('grades', grade.id)}
                            disabled={isLoading}
                          >
                            <i className="fas fa-edit"></i>
                          </button>
                          <button 
                            className="delete-btn"
                            onClick={() => handleDelete(grade.id, 'grade')}
                            disabled={isLoading}
                          >
                            <i className="fas fa-trash"></i>
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
              
              <div className="pagination">
                <button 
                  onClick={() => fetchGrades(pagination.grades.page - 1)}
                  disabled={pagination.grades.page <= 1 || isLoading}
                >
                  Назад
                </button>
                <span>
                  Страница {pagination.grades.page} из {' '}
                  {Math.ceil(pagination.grades.total / pagination.grades.perPage)}
                </span>
                <button 
                  onClick={() => fetchGrades(pagination.grades.page + 1)}
                  disabled={
                    pagination.grades.page * pagination.grades.perPage >= 
                    pagination.grades.total || isLoading
                  }
                >
                  Вперед
                </button>
              </div>
            </div>
          )}

          {activeTab === 'stats' && (
            <div className="stats-section">
              <div className="section-header">
                <h2>Статистика успеваемости</h2>
                <button 
                  className="refresh-btn"
                  onClick={fetchStats}
                  disabled={isLoading}
                >
                  <i className="fas fa-sync-alt"></i> Обновить
                </button>
              </div>
              
              <div className="stats-grid">
                <div className="stat-card">
                  <div className="stat-icon bg-red">
                    <i className="fas fa-exclamation-triangle"></i>
                  </div>
                  <div className="stat-info">
                    <h3>Самый сложный предмет</h3>
                    <p>{stats.worstSubject || 'Нет данных'}</p>
                  </div>
                </div>

                <div className="stat-card">
                  <div className="stat-icon bg-orange">
                    <i className="fas fa-user-times"></i>
                  </div>
                  <div className="stat-info">
                    <h3>Неуспевающие ученики</h3>
                    <p>{stats.failingStudents}</p>
                  </div>
                </div>

                <div className="stat-card">
                  <div className="stat-icon bg-purple">
                    <i className="fas fa-chalkboard-teacher"></i>
                  </div>
                  <div className="stat-info">
                    <h3>Самый строгий учитель</h3>
                    <p>{stats.worstTeacher || 'Нет данных'}</p>
                  </div>
                </div>

                <div className="stat-card">
                  <div className="stat-icon bg-blue">
                    <i className="fas fa-users"></i>
                  </div>
                  <div className="stat-info">
                    <h3>Средние оценки по классам</h3>
                    <ul>
                      {Object.entries(stats.classAverages).map(([cls, avg]) => (
                        <li key={cls}>
                          {cls}: {typeof avg === 'number' ? avg.toFixed(1) : 'Нет данных'}
                        </li>
                      ))}
                    </ul>
                  </div>
                </div>

                <div className="stat-card">
                  <div className="stat-icon bg-green">
                    <i className="fas fa-trophy"></i>
                  </div>
                  <div className="stat-info">
                    <h3>Лучший класс</h3>
                    <p>{stats.bestClass || 'Нет данных'}</p>
                  </div>
                </div>

                <div className="stat-card">
                  <div className="stat-icon bg-red">
                    <i className="fas fa-sad-tear"></i>
                  </div>
                  <div className="stat-info">
                    <h3>Худший класс</h3>
                    <p>{stats.worstClass || 'Нет данных'}</p>
                  </div>
                </div>
              </div>
            </div>
          )}

          {activeTab === 'users' && (
            <div className="table-section">
              <div className="section-header">
                <h2>Список пользователей</h2>
                <div className="section-actions">
                  <button 
                    className="export-btn"
                    onClick={() => exportData('users')}
                    disabled={isLoading}
                  >
                    <i className="fas fa-file-export"></i> Экспорт
                  </button>
                  <button 
                    className="add-btn" 
                    onClick={() => handleAdd('user')}
                    disabled={isLoading}
                  >
                    <i className="fas fa-plus"></i> Добавить
                  </button>
                </div>
              </div>
              
              <div className="filters">
                <select
                  value={filters.users.role}
                  onChange={(e) => setFilters({
                    ...filters, 
                    users: {
                      ...filters.users, 
                      role: e.target.value 
                    }
                  })}
                >
                  <option value="">Все роли</option>
                  <option value="Admin">Администратор</option>
                  <option value="Teacher">Учитель</option>
                  <option value="Student">Ученик</option>
                  <option value="User">Пользователь</option>
                </select>
              </div>
              
              <div className="table-container">
                <table>
                  <thead>
                    <tr>
                      <th>ID</th>
                      <th>Логин</th>
                      <th>Роль</th>
                      <th>Действия</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredData(users, 'users').map(user => (
                      <tr key={user.UserID}>
                        <td>{user.UserID}</td>
                        <td>{user.Username}</td>
                        <td>{user.Role}</td>
                        <td className="actions">
                          <button 
                            className="edit-btn"
                            onClick={() => handleEdit('user', user.UserID)}
                            disabled={isLoading || user.Role === 'Admin'}
                          >
                            <i className="fas fa-edit"></i>
                          </button>
                          <button 
                            className="delete-btn"
                            onClick={() => handleDelete(user.UserID, 'user')}
                            disabled={isLoading || user.Role === 'Admin'}
                          >
                            <i className="fas fa-trash"></i>
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
              
              <div className="pagination">
                <button 
                  onClick={() => fetchUsers(pagination.users.page - 1)}
                  disabled={pagination.users.page <= 1 || isLoading}
                >
                  Назад
                </button>
                <span>
                Страница {pagination.users.page} из {' '}
                {Math.ceil(pagination.users.total / pagination.users.perPage)}
                </span>
                <button 
                  onClick={() => fetchUsers(pagination.users.page + 1)}
                  disabled={
                    pagination.users.page * pagination.users.perPage >= 
                    pagination.users.total || isLoading
                  }
                >
                  Вперед
                </button>
              </div>
            </div>
          )}
        </main>
      </div>

      {/* Модальное окно подтверждения выхода */}
      {showLogoutModal && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>Подтверждение выхода</h2>
            <p>Вы уверены, что хотите выйти из системы?</p>
            <div className="modal-actions">
              <button 
                className="cancel-btn"
                onClick={cancelLogout}
              >
                Остаться
              </button>
              <button 
                className="confirm-btn"
                onClick={confirmLogout}
              >
                Да, выйти
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Модальное окно для добавления/редактирования */}
      {showAddModal && (
        <div className="modal-overlay">
          <div className="modal">
            <button 
              className="close-modal" 
              onClick={() => {
                setShowAddModal(false);
                resetForm();
              }}
              disabled={isLoading}
            >
              <i className="fas fa-times"></i>
            </button>
            
            <h2>
              {modalType === 'teacher' && 'Назначить учителя'}
              {modalType === 'edit_teacher' && 'Редактировать учителя'}
              {modalType === 'student' && 'Добавить ученика'}
              {modalType === 'edit_student' && 'Редактировать ученика'}
              {modalType === 'grades' && 'Добавить оценки'}
              {modalType === 'edit_grades' && 'Редактировать оценки'}
              {modalType === 'user' && 'Добавить пользователя'}
              {modalType === 'edit_user' && 'Редактировать пользователя'}
            </h2>
            
            {error && <div className="error-message">{error}</div>}
            
            <form 
              onSubmit={
                modalType.includes('user') ? handleUserSubmit : handleSubmit
              }
            >
              {(modalType === 'user' || modalType === 'edit_user') && (
                <>
                  <div className="form-group">
                    <label>Логин</label>
                    <input 
                      type="text" 
                      value={newUser.username}
                      onChange={(e) => setNewUser({
                        ...newUser, 
                        username: e.target.value
                      })}
                      required
                      disabled={isLoading || modalType === 'edit_user'}
                    />
                  </div>
                  
                  {modalType === 'user' && (
                    <>
                      <div className="form-group">
                        <label>Пароль</label>
                        <input 
                          type="password" 
                          value={newUser.password}
                          onChange={(e) => setNewUser({
                            ...newUser, 
                            password: e.target.value
                          })}
                          required
                          disabled={isLoading}
                        />
                      </div>
                      <div className="form-group">
                        <label>Подтверждение пароля</label>
                        <input 
                          type="password" 
                          value={newUser.confirmPassword}
                          onChange={(e) => setNewUser({
                            ...newUser, 
                            confirmPassword: e.target.value
                          })}
                          required
                          disabled={isLoading}
                        />
                      </div>
                    </>
                  )}
                  
                  <div className="form-group">
                    <label>Роль</label>
                    <select
                      value={newUser.role}
                      onChange={(e) => setNewUser({
                        ...newUser, 
                        role: e.target.value
                      })}
                      required
                      disabled={isLoading}
                    >
                      <option value="User">Пользователь</option>
                      <option value="Student">Ученик</option>
                      <option value="Teacher">Учитель</option>
                      <option value="Admin">Администратор</option>
                    </select>
                  </div>
                </>
              )}

              {modalType === 'teacher' && (
                <>
                  <div className="form-group">
                    <label>Пользователь</label>
                    <select
                      value={selectedUserId}
                      onChange={(e) => setSelectedUserId(Number(e.target.value))}
                      required
                      disabled={isLoading}
                    >
                      <option value="">— Выберите пользователя —</option>
                      {users.filter(u => u.Role === 'Teacher').map(u => (
                        <option key={u.UserID} value={u.UserID}>
                          {u.Username} (ID={u.UserID})
                        </option>
                      ))}
                    </select>
                  </div>
                  <div className="form-group">
                    <label>ФИО</label>
                    <input 
                      type="text" 
                      placeholder="Полное имя"
                      value={fullName}
                      onChange={(e) => setFullName(e.target.value)}
                      required
                      disabled={isLoading}
                    />
                  </div>
                  <div className="form-group">
                    <label>Кабинет</label>
                    <input 
                      type="number" 
                      placeholder="Номер кабинета"
                      value={classroomNumber}
                      onChange={(e) => setClassroomNumber(e.target.value)}
                      disabled={isLoading}
                    />
                  </div>
                </>
              )}

              {modalType === 'edit_teacher' && (
                <>
                  <div className="form-group">
                    <label>ФИО</label>
                    <input 
                      type="text" 
                      value={fullName}
                      onChange={(e) => setFullName(e.target.value)}
                      required
                      disabled={isLoading}
                    />
                  </div>
                  <div className="form-group">
                    <label>Кабинет</label>
                    <input 
                      type="number" 
                      value={classroomNumber}
                      onChange={(e) => setClassroomNumber(e.target.value)}
                      disabled={isLoading}
                    />
                  </div>
                </>
              )}

              {(modalType === 'student' || modalType === 'edit_student') && (
                <>  
                  <div className="form-group">
                    <label>ФИО</label>
                    <input 
                      type="text" 
                      value={newStudent.name}
                      onChange={(e) => setNewStudent({
                        ...newStudent, 
                        name: e.target.value
                      })}
                      required
                      disabled={isLoading}
                    />
                  </div>
                  <div className="form-group">
                    <label>Класс</label>
                    <select
                      value={newStudent.class}
                      onChange={(e) => setNewStudent({
                        ...newStudent, 
                        class: e.target.value
                      })}
                      required
                      disabled={isLoading}
                    >
                      <option value="">Выберите класс</option>
                      <option value="10А">10А</option>
                      <option value="10Б">10Б</option>
                      <option value="9А">9А</option>
                      <option value="9Б">9Б</option>
                    </select>
                  </div>
                </>
              )}

              {(modalType === 'grades' || modalType === 'edit_grades') && (
                <>
                  <div className="form-group">
                    <label>Ученик</label>
                    <select
                      value={newGrades.student}
                      onChange={(e) => setNewGrades({
                        ...newGrades, 
                        student: e.target.value
                      })}
                      required
                      disabled={isLoading}
                    >
                      <option value="">Выберите ученика</option>
                      {students.map(student => (
                        <option key={student.id} value={student.fullName}>
                          {student.fullName} ({student.className})
                        </option>
                      ))}
                    </select>
                  </div>
                  <div className="grades-grid">
                    <div className="form-group">
                      <label>Математика</label>
                      <select
                        value={newGrades.subject1}
                        onChange={(e) => setNewGrades({
                          ...newGrades, 
                          subject1: e.target.value
                        })}
                        required
                        disabled={isLoading}
                      >
                        <option value="">Выберите оценку</option>
                        <option value="5">5 (Отлично)</option>
                        <option value="4">4 (Хорошо)</option>
                        <option value="3">3 (Удовлетворительно)</option>
                        <option value="2">2 (Неудовлетворительно)</option>
                      </select>
                    </div>
                    <div className="form-group">
                      <label>Физика</label>
                      <select
                        value={newGrades.subject2}
                        onChange={(e) => setNewGrades({
                          ...newGrades, 
                          subject2: e.target.value
                        })}
                        required
                        disabled={isLoading}
                      >
                        <option value="">Выберите оценку</option>
                        <option value="5">5 (Отлично)</option>
                        <option value="4">4 (Хорошо)</option>
                        <option value="3">3 (Удовлетворительно)</option>
                        <option value="2">2 (Неудовлетворительно)</option>
                      </select>
                    </div>
                    <div className="form-group">
                      <label>Литература</label>
                      <select
                        value={newGrades.subject3}
                        onChange={(e) => setNewGrades({
                          ...newGrades, 
                          subject3: e.target.value
                        })}
                        required
                        disabled={isLoading}
                      >
                        <option value="">Выберите оценку</option>
                        <option value="5">5 (Отлично)</option>
                        <option value="4">4 (Хорошо)</option>
                        <option value="3">3 (Удовлетворительно)</option>
                        <option value="2">2 (Неудовлетворительно)</option>
                      </select>
                    </div>
                  </div>
                </>
              )}

              <div className="form-actions">
                <button 
                  type="button" 
                  className="cancel-btn" 
                  onClick={() => {
                    setShowAddModal(false);
                    resetForm();
                  }}
                  disabled={isLoading}
                >
                  Отмена
                </button>
                <button 
                  type="submit" 
                  className="submit-btn"
                  disabled={isLoading}
                >
                  {isLoading ? (
                    <>
                      <i className="fas fa-spinner fa-spin"></i> Обработка...
                    </>
                  ) : modalType.startsWith('edit_') ? 'Сохранить изменения' : 'Добавить'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
